void splashRender()
{
   // Clear screen
   int wrofs = 0;
   for( wrofs=0; wrofs<SCREEN_HEIGHT*SCREEN_WIDTH*4; wrofs++ )
      screenBuffer[ wrofs ] = 0.0f;

   wrofs = ((((SCREEN_HEIGHT-jrazlogo_height) / 2) * SCREEN_WIDTH) + ((SCREEN_WIDTH-jrazlogo_width) / 2)) * 4;
   int rdofs = 0;
   int x, y;
   for( y=0; y<jrazlogo_height; y++ )
   {
      for( x=0; x<jrazlogo_width; x++ )
      {
         screenBuffer[ wrofs++ ] = jrazlogo_pixels[ rdofs++ ];
         screenBuffer[ wrofs++ ] = jrazlogo_pixels[ rdofs++ ];
         screenBuffer[ wrofs++ ] = jrazlogo_pixels[ rdofs++ ];
         wrofs++; // Skip fourth output channel (depth)
      }
	   
	   wrofs += (SCREEN_WIDTH-jrazlogo_width)*4;
   }
}
